srcDoc.js:
appsmith.onReady(() => {
  const input = document.getElementById('input');
  const button = document.getElementById('button');
  button.innerHTML = appsmith.model.name;
  input.addEventListener("keyup", () => {
    const output = document.getElementById('output');
    const value = input.value;
    if (value.trim() !== '') {
      output.textContent = `You entered: ${value}`;
    } else {
      output.textContent = 'Please enter something.';
    }
    appsmith.updateModel({
      value: input.value
    });
  });
  button.addEventListener("click", () => {
    appsmith.triggerEvent("on1Click");
  });
  appsmith.onModelChange(model => {
    button.innerHTML = model.name;
  });
});

